result="{"; append_to_result() { key="$1"; value="$2"; if [ "${result}" != "{" ]; then result="$result,"; fi; result="$result\"$key\":\"$value\""; }; apache_version=$(httpd -v 2>&1 | grep -oP 'Apache/\K[^ ]+'); if [ $? -eq 0 ]; then append_to_result "apache_version" "$apache_version"; fi; nginx_version=$(nginx -v 2>&1 | grep -oP 'nginx/\K[^ ]+'); if [ $? -eq 0 ]; then append_to_result "nginx_version" "$nginx_version"; fi; mysql_version=$(mysql -V 2>&1 | grep -oP '\d+\.\d+\.\d+'); if [ $? -eq 0 ]; then append_to_result "mysql_version" "$mysql_version"; fi; composer_version=$(composer --version 2>&1 | grep -oP 'Composer \K\d+\.\d+\.\d+'); if [ $? -eq 0 ]; then append_to_result "composer_version" "$composer_version"; fi; git_version=$(git --version 2>&1 | grep -oP '\d+\.\d+\.\d+'); if [ $? -eq 0 ]; then append_to_result "git_version" "$git_version"; fi; php_version=$(php -v 2>&1 | grep -oP 'PHP \K\d+\.\d+\.\d+'); if [ $? -eq 0 ]; then append_to_result "php_version" "$php_version"; fi; modules_array=""; php_modules=$(php -m | tail -n +2); for line in $php_modules; do if [ -n "$line" ]; then if [ -n "$modules_array" ]; then modules_array="$modules_array,"; fi; modules_array="$modules_array\"$line\""; fi; done; if [ -n "$modules_array" ]; then result="$result,\"modules\":[${modules_array}]"; fi; result="$result}"; echo "$result";